mapper.select:
SELECT


FROM nj_report_statistics
WHERE 1 = 1
and serial_number LIKE '%${serial_number}%'
and template_name = #{template_name}
and path = #{path}
and creat_time >= #{startTime}
and creat_time  <= #{endTime}
ORDER BY creat_time LIMIT #{offset},#{pageSize}

SELECT COUNT(id)
FROM nj_report_statistics
WHERE 1 = 1
and serial_number LIKE '%${serial_number}%'
and template_name = #{template_name}
and path = #{path}
and creat_time >= #{startTime}
and creat_time  <= #{endTime}

mapper.insert:
INSERT INTO nj_report_statistics(
serial_number, template_name, path, creat_time, down_number,
) VALUES (
#{serial_number}, #{template_name}, #{path}, #{creat_time}, #{down_number},
)

mapper.delete:
DELETE FROM nj_report_statistics WHERE 1 = 1
and id = #{id}
and serial_number = #{serial_number}
and template_name = #{template_name}
and path = #{path}
and creat_time = #{creat_time}

mapper.update:
update nj_report_statistics
serial_number = #{serial_number}, template_name = #{template_name}, path = #{path}, creat_time = #{creat_time}, down_number = #{down_number},
where id=#{id}
